<div data-result-type="ads" style="display: none;">
    <h6 class="fw-bold mb-3">Ad Copy</h6>
    
    <div class="mb-4">
        <label class="form-label fw-bold">Headlines</label>
        <div id="adHeadlines" class="space-y-2 mb-3"></div>
        <button class="btn btn-outline-primary btn-sm" data-action="copy-headlines">
            <i class="fas fa-copy me-1"></i> Copy All Headlines
        </button>
    </div>

    <div class="mb-4">
        <label class="form-label fw-bold">Descriptions</label>
        <div id="adDescriptions" class="space-y-2 mb-3"></div>
        <button class="btn btn-outline-primary btn-sm" data-action="copy-descriptions">
            <i class="fas fa-copy me-1"></i> Copy All Descriptions
        </button>
    </div>

    <div class="mb-4">
        <label class="form-label fw-bold">Call to Action</label>
        <input type="text" id="adsCTA" class="form-control" readonly>
        <button class="btn btn-outline-primary btn-sm mt-2" data-target="adsCTA">
            <i class="fas fa-copy me-1"></i> Copy CTA
        </button>
    </div>

    <div class="alert alert-info">
        <strong>Tip:</strong> Use these headlines and descriptions in Google Ads Manager or Facebook Ads Manager
    </div>

    <div class="mt-4">
        <button class="btn btn-success" data-action="mark-used">
            <i class="fas fa-check me-1"></i> Mark as Used
        </button>
    </div>
</div>
